mapper.select:
SELECT d.id, d.id_device, d.id_project, d.id_device_type, d.`name`, d.model, d.serial_number, d.manufacturer, DATE_FORMAT(d.installed_at,'%d/%m/%Y') AS installed_at, d.`status`, dtd.`name` AS device_type_name, dg.`name` AS device_group_name, dg.table_name, d.id_device_group, p.hash_id, IF(d.power_now IS NULL OR DATE_FORMAT(NOW(),'%k') >= 19 OR DATE_FORMAT(NOW(),'%k') <= 5, NULL, (d.power_now / 1000)) AS power_now, IF(d.energy_today IS NULL, NULL, (d.energy_today / 1000)) AS energy_today, IF(d.last_month IS NULL, NULL, (d.last_month / 1000)) AS last_month, d.lifetime, FROM_UNIXTIME(d.last_updated, '%d/%m/%Y %H:%i') AS last_updated FROM device d LEFT JOIN device_type dt ON dt.id = d.id_device_type LEFT JOIN device_type_detail dtd ON dtd.id_device_type = dt.id AND dtd.id_language = #{id_language}
LEFT JOIN device_group dg ON dg.id = d.id_device_group LEFT JOIN project p ON p.id = d.id_project LEFT JOIN emloyee_project_map epm ON epm.id_project = p.id
WHERE p.`status` = 1 AND d.`status` IN ( 1, 0) AND epm.id_employee = #{id_employee} AND d.is_virtual = 1 AND d.id IN ( SELECT d.id FROM project_map_device_virtual pm LEFT JOIN device d ON d.id = pm.id_device LEFT JOIN project p ON p.id = pm.id_project WHERE p.hash_id = #{hash_id} AND p.`status` = 1 AND d.`status` = 1 AND d.is_virtual = 1 GROUP BY d.id UNION ALL SELECT d.id FROM device d LEFT JOIN project p ON p.id = d.id_project WHERE p.hash_id = #{hash_id} AND p.`status` = 1 AND d.`status` = 1 AND d.is_virtual = 1 )
AND ( d.`name` LIKE CONCAT('%',#{name},'%') )
AND ( d.`id_device` LIKE CONCAT('%',#{id_device},'%') )
ORDER BY ${sort_column} ${order_by}
ORDER BY d.id DESC
LIMIT ${offset}, ${limit}

SELECT dv.id_device, IF(dv.activePower IS NULL OR DATE_FORMAT(NOW(),'%k') >= 19 OR DATE_FORMAT(NOW(),'%k') <= 5, NULL, dv.activePower) AS power_now, dv.activeEnergy AS lifetime, IFNULL(t.today_activeEnergy,0) AS today_activeEnergy, IFNULL(m.this_month,0) AS this_month
FROM ${table_name} dv LEFT JOIN ( SELECT dv.time, dv.id_device, (MAX( dv.activeEnergy ) ) - (MIN( dv.activeEnergy )) AS today_activeEnergy, MAX(dv.activeEnergy) AS lifetime FROM ${table_name} dv WHERE FROM_UNIXTIME( dv.time, '%d/%m/%Y' ) = DATE_FORMAT( #{current_date}, '%d/%m/%Y') AND dv.id_device = #{id_device} GROUP BY dv.id_device )t ON dv.id_device = t.id_device
LEFT JOIN ( SELECT dv.time, dv.id_device, (MAX( dv.activeEnergy ) ) - (MIN( dv.activeEnergy )) AS this_month FROM ${table_name} dv WHERE FROM_UNIXTIME( dv.time, '%m/%Y' ) = DATE_FORMAT( #{current_date}, '%m/%Y') AND dv.id_device = #{id_device} AND dv.activeEnergy > 0 GROUP BY dv.id_device )m ON dv.id_device = m.id_device
WHERE dv.id_device = #{id_device}
ORDER BY dv.time DESC LIMIT 1;

SELECT a.id, a.id_error, a.id_alert_state, FROM_UNIXTIME(a.start_date, '%d/%m/%Y %H:%i:%s') AS start_date_format, CASE WHEN FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') < DATE_ADD( NOW( ), INTERVAL - 1 DAY ) THEN TIMESTAMPDIFF( DAY, FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s'), NOW( ) ) WHEN FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') < DATE_ADD( NOW( ), INTERVAL - 1 HOUR ) THEN TIMESTAMPDIFF( HOUR, FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s'), NOW( ) ) WHEN FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') < DATE_ADD( NOW( ), INTERVAL - 1 MINUTE ) THEN TIMESTAMPDIFF( MINUTE, FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s'), NOW( ) ) ELSE 0 END AS times_ago, CASE WHEN FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') < DATE_ADD( NOW( ), INTERVAL - 1 DAY ) THEN 'DAY' WHEN FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') < DATE_ADD( NOW( ), INTERVAL - 1 HOUR ) THEN 'HOUR' WHEN FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') < DATE_ADD( NOW( ), INTERVAL - 1 MINUTE ) THEN 'MINUTE' ELSE 'NOW' END AS times_ago_unit,
a.end_date, a.`status`, a.note, ed.message, ed.description, ed.solutions, eld.`name` as error_level_name, etd.`name` AS error_type_name, el.thumbnail, e.id_error_level FROM alert a LEFT JOIN error e ON e.id = a.id_error LEFT JOIN error_detail ed ON ed.id_error = e.id AND ed.id_language = #{id_language}
LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN error_level_detail eld ON eld.id_error_level = el.id AND eld.id_language = #{id_language}
LEFT JOIN error_type et ON et.id = e.id_error_type LEFT JOIN error_type_detail etd ON etd.id_error_type = et.id AND etd.id_language = #{id_language} WHERE a.id_device = #{id} AND a.`status` IN(0,1) AND el.`status` = 1 AND et.`status` = 1 AND e.`status` = 1
AND ( e.`id_error_level` = #{id_error_level} )
AND ( e.`id_error_type` = #{id_error_type} )
AND FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') BETWEEN CAST(#{date_from} AS DATE) AND CAST(#{date_to} AS DATE)
ORDER BY ${sort_column} ${order_by}
ORDER BY a.id DESC
LIMIT ${offset}, ${limit}

SELECT
	count(a.id) totalRow
FROM
	alert a 
	LEFT JOIN error e ON e.id = a.id_error
	LEFT JOIN error_detail ed ON ed.id_error = e.id AND ed.id_language = #{id_language}

	LEFT JOIN error_level el ON el.id = e.id_error_level
	LEFT JOIN error_level_detail eld ON eld.id_error_level = el.id AND eld.id_language = #{id_language}

	LEFT JOIN error_type et ON et.id = e.id_error_type
	LEFT JOIN error_type_detail etd ON etd.id_error_type = et.id AND etd.id_language = #{id_language}
WHERE
	a.id_device = #{id} AND a.`status` IN(0,1) AND el.`status` = 1 AND et.`status` = 1 AND e.`status` = 1

		AND  (
			e.`id_error_level` = #{id_error_level}
		)



		AND  (
			e.`id_error_type` = #{id_error_type}
		)


		AND FROM_UNIXTIME(a.start_date, '%Y-%m-%d %H:%i:%s') BETWEEN CAST(#{date_from} AS DATE) AND CAST(#{date_to} AS DATE)
